var list=[];function createLi(i){list.map(function(i,s){$(".dianqi-ul").append("<li><div class='img'><img src = "+i.img+"></div><div class='p00'>"+i.title+"</div><div class='p11'>"+i.lable+"</div><div class='p10'>"+i.price+"</div><button class='p12' onclick='getInfo("+s+")'>加入购物车</button></li>")})}$.ajax({type:"GET",url:"../list.json",dataType:"json",success:function(i){console.log(i),createLi(list=i)}});